import React from 'react';
import { useParams } from 'react-router-dom';
import './CourseDetail.css';

const CourseDetail = () => {
  const { id } = useParams();

  // Données temporaires (à remplacer par l'API plus tard)
  const courseDetails = {
    1: {
      title: 'Guitare - Niveau Débutant',
      description: 'Apprenez la guitare à votre rythme',
      level: 'Débutant',
      imageUrl: 'https://images.unsplash.com/photo-1510915361894-db8b60106cb1?ixlib=rb-1.2.1&auto=format&fit=crop&w=1350&q=80',
      duration: '3 mois',
      price: '299€',
      objectives: [
        'Maîtriser les accords de base',
        'Apprendre les techniques de base',
        'Développer votre sens du rythme',
        'Jouer vos premiers morceaux'
      ],
      program: [
        {
          week: 1,
          title: 'Introduction à la guitare',
          content: 'Découverte de l\'instrument, accordage, position de base'
        },
        {
          week: 2,
          title: 'Les accords de base',
          content: 'Apprentissage des accords majeurs et mineurs'
        },
        {
          week: 3,
          title: 'Le rythme',
          content: 'Exercices rythmiques et premiers arpèges'
        },
        {
          week: 4,
          title: 'Premiers morceaux',
          content: 'Application des connaissances sur des morceaux simples'
        }
      ],
      requirements: [
        'Avoir une guitare',
        'Être motivé et assidu',
        'Avoir 30 minutes par jour pour pratiquer'
      ],
      teacher: {
        name: 'Jean Dupont',
        bio: 'Guitariste professionnel avec 15 ans d\'expérience',
        imageUrl: 'https://images.unsplash.com/photo-1507003211169-0a1dd7228f2d?ixlib=rb-1.2.1&auto=format&fit=crop&w=1350&q=80'
      }
    },
    // Ajoutez les autres cours ici...
  };

  const course = courseDetails[id];

  if (!course) {
    return <div className="error">Cours non trouvé</div>;
  }

  return (
    <div className="course-detail">
      <div className="course-header">
        <div className="course-header-content">
          <h1>{course.title}</h1>
          <p className="course-level">{course.level}</p>
          <p className="course-description">{course.description}</p>
          <div className="course-meta">
            <span className="duration">Durée: {course.duration}</span>
            <span className="price">Prix: {course.price}</span>
          </div>
        </div>
        <div className="course-image">
          <img src={course.imageUrl} alt={course.title} />
        </div>
      </div>

      <div className="course-content">
        <section className="objectives">
          <h2>Objectifs du cours</h2>
          <ul>
            {course.objectives.map((objective, index) => (
              <li key={index}>{objective}</li>
            ))}
          </ul>
        </section>

        <section className="program">
          <h2>Programme</h2>
          <div className="program-timeline">
            {course.program.map((week) => (
              <div key={week.week} className="program-week">
                <div className="week-number">Semaine {week.week}</div>
                <div className="week-content">
                  <h3>{week.title}</h3>
                  <p>{week.content}</p>
                </div>
              </div>
            ))}
          </div>
        </section>

        <section className="requirements">
          <h2>Prérequis</h2>
          <ul>
            {course.requirements.map((requirement, index) => (
              <li key={index}>{requirement}</li>
            ))}
          </ul>
        </section>

        <section className="teacher">
          <h2>Votre professeur</h2>
          <div className="teacher-profile">
            <img src={course.teacher.imageUrl} alt={course.teacher.name} />
            <div className="teacher-info">
              <h3>{course.teacher.name}</h3>
              <p>{course.teacher.bio}</p>
            </div>
          </div>
        </section>

        <div className="course-actions">
          <button className="enroll-button">S'inscrire au cours</button>
          <button className="contact-button">Contacter le professeur</button>
        </div>
      </div>
    </div>
  );
};

export default CourseDetail;
