import React from 'react';
import { useParams } from 'react-router-dom';

const CourseDetail = () => {
  const { id } = useParams();

  // Données temporaires (à remplacer par l'API plus tard)
  const courses = [
    // Guitare
    {
      id: 1,
      title: "Guitare - Niveau Débutant",
      description: "Apprenez les bases de la guitare avec ce cours complet pour débutants. Vous découvrirez les accords essentiels, les techniques de base et commencerez à jouer vos premiers morceaux.",
      level: "Débutant",
      image: "https://images.unsplash.com/photo-1510915361894-db8b60106cb1?ixlib=rb-1.2.1&auto=format&fit=crop&w=1350&q=80",
      duration: "8 semaines",
      price: "299€",
      objectives: [
        "Maîtriser les accords de base (Mi, La, Ré, Sol, Do)",
        "Apprendre les techniques de base (picking, strumming)",
        "Jouer des morceaux simples",
        "Comprendre la lecture de tablatures"
      ],
      program: [
        {
          week: 1,
          title: "Introduction à la guitare",
          content: "Découverte de l'instrument, accordage, position de base, premiers accords"
        },
        {
          week: 2,
          title: "Techniques de base",
          content: "Picking, strumming, exercices de coordination"
        },
        {
          week: 3,
          title: "Accords et rythmes",
          content: "Nouveaux accords, patterns rythmiques, premiers morceaux"
        },
        {
          week: 4,
          title: "Premiers morceaux",
          content: "Apprentissage de morceaux simples, travail sur la fluidité"
        }
      ],
      requirements: [
        "Avoir une guitare",
        "Pratiquer régulièrement (minimum 30 minutes par jour)",
        "Avoir un accordeur",
        "Être motivé et patient"
      ],
      teacher: {
