            <div className="relative pl-8 space-y-8">
              {course.program.map((week) => (
                <div key={week.week} className="relative">
                  <div className="absolute left-0 top-0 -ml-4 flex h-8 w-8 items-center justify-center rounded-full bg-blue-100">
                    <span className="text-sm font-medium text-blue-800">{week.week}</span>
                  </div>
                  <div className="bg-gray-50 rounded-xl p-6">
