              <div className="text-center md:text-left">
                <h3 className="text-xl font-semibold text-gray-900 mb-2">{course.teacher.name}</h3>
                <p className="text-gray-600">{course.teacher.bio}</p>
              </div>
            </div>
          </div>

          <div className="flex flex-col sm:flex-row gap-4 lg:col-span-2 mt-8">
            <button className="flex-1 bg-blue-600 text-white px-8 py-4 rounded-xl font-semibold hover:bg-blue-700 transition-colors duration-200">
