import React, { useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { motion } from 'framer-motion';

const CourseDetail = () => {
  const { id } = useParams();

  useEffect(() => {
    window.scrollTo(0, 0);
  }, []);

  // Simuler les données du cours (à remplacer par une vraie API)
  const course = {
    id: parseInt(id),
    title: id === "5" ? "Piano - Niveau Intermédiaire" : id === "6" ? "Piano - Niveau Avancé" : "Piano - Niveau Débutant",
    description: id === "5" 
      ? "Développez votre technique pianistique avec nos professeurs expérimentés"
      : id === "6"
      ? "Maîtrisez les techniques avancées du piano et développez votre interprétation"
      : "Découvrez les bases du piano avec nos professeurs expérimentés",
    image: "https://images.unsplash.com/photo-1520523839897-bd0b52f945a0?ixlib=rb-1.2.1&auto=format&fit=crop&w=1350&q=80",
    price: id === "5" ? "349€" : id === "6" ? "399€" : "299€",
    duration: "8 semaines",
    level: id === "5" ? "Intermédiaire" : id === "6" ? "Avancé" : "Débutant",
    objectives: id === "5" ? [
      "Perfectionner la technique pianistique",
      "Maîtriser les gammes et arpèges",
      "Développer l'interprétation musicale",
      "Jouer des morceaux de niveau intermédiaire"
    ] : id === "6" ? [
      "Maîtriser les techniques avancées",
      "Développer une interprétation personnelle",
      "Explorer différents styles musicaux",
      "Préparer des pièces de concert"
    ] : [
      "Maîtriser la position des mains",
      "Apprendre à lire une partition simple",
      "Développer votre sens du rythme",
      "Jouer vos premiers morceaux"
    ],
    program: id === "5" ? [
      {
        week: 1,
        title: "Technique avancée",
        content: "Perfectionnement de la technique, gammes et arpèges"
      },
      {
        week: 2,
        title: "Interprétation",
        content: "Développement de l'expression musicale et du phrasé"
      },
      {
        week: 3,
        title: "Styles musicaux",
        content: "Exploration de différents styles et répertoires"
      },
      {
        week: 4,
        title: "Morceaux de niveau intermédiaire",
        content: "Travail sur des pièces de niveau intermédiaire"
      }
    ] : id === "6" ? [
      {
        week: 1,
        title: "Techniques avancées",
        content: "Maîtrise des techniques pianistiques avancées"
      },
      {
        week: 2,
        title: "Interprétation personnelle",
        content: "Développement d'une interprétation unique et expressive"
      },
      {
        week: 3,
        title: "Répertoire avancé",
        content: "Exploration du répertoire pianistique avancé"
      },
      {
        week: 4,
        title: "Préparation de concert",
        content: "Travail sur des pièces de concert et performance"
      }
    ] : [
      {
        week: 1,
        title: "Introduction au piano",
        content: "Découverte de l'instrument, position des mains, premiers exercices"
      },
      {
        week: 2,
        title: "Les notes de base",
        content: "Apprentissage des notes sur la portée et sur le clavier"
      },
      {
        week: 3,
        title: "Le rythme",
        content: "Exercices rythmiques et patterns de base"
      },
      {
        week: 4,
        title: "Premiers morceaux",
        content: "Application des connaissances sur des morceaux simples"
      }
    ],
    teacher: {
      name: "Marie Martin",
      role: "Professeur de piano",
      experience: "15 ans d'expérience",
      image: "https://images.unsplash.com/photo-1494790108377-be9c29b29330?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=facearea&facepad=2&w=256&h=256&q=80"
    }
  };

  const containerVariants = {
    hidden: { opacity: 0 },
    visible: {
      opacity: 1,
      transition: {
        staggerChildren: 0.1
      }
    }
  };

  const itemVariants = {
    hidden: { opacity: 0, y: 20 },
    visible: {
      opacity: 1,
      y: 0,
      transition: {
        duration: 0.5
      }
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-b from-gray-50 to-blue-50 py-12">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        {/* En-tête avec animation */}
        <motion.div 
          initial={{ opacity: 0, y: -20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.6 }}
          className="text-center mb-16"
        >
          <h1 className="text-5xl font-bold text-gray-900 mb-4 bg-clip-text text-transparent bg-gradient-to-r from-blue-600 to-purple-600">
            {course.title}
          </h1>
          <p className="text-xl text-gray-600 max-w-3xl mx-auto">
            {course.description}
          </p>
        </motion.div>

        <div className="grid grid-cols-1 lg:grid-cols-3 gap-12">
          {/* Informations principales avec animation */}
          <motion.div 
            initial={{ opacity: 0, x: -50 }}
            animate={{ opacity: 1, x: 0 }}
            transition={{ duration: 0.6, delay: 0.2 }}
            className="lg:col-span-2 space-y-8"
          >
            {/* Image du cours */}
            <motion.div 
              whileHover={{ scale: 1.02 }}
              className="relative h-96 rounded-2xl overflow-hidden shadow-lg"
            >
              <img
                src={course.image}
                alt={course.title}
                className="w-full h-full object-cover"
              />
              <div className="absolute top-4 right-4 bg-blue-600 text-white px-4 py-2 rounded-full text-sm font-semibold">
                {course.level}
              </div>
            </motion.div>

            {/* Objectifs */}
            <motion.div 
              variants={containerVariants}
              initial="hidden"
              animate="visible"
              className="bg-white rounded-2xl shadow-lg p-8"
            >
              <h2 className="text-2xl font-bold text-gray-900 mb-6">Objectifs du cours</h2>
              <ul className="space-y-4">
                {course.objectives.map((objective, index) => (
                  <motion.li
                    key={index}
                    variants={itemVariants}
                    className="flex items-start space-x-3"
                  >
                    <svg className="h-6 w-6 text-blue-600 flex-shrink-0" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M5 13l4 4L19 7" />
                    </svg>
                    <span className="text-gray-600">{objective}</span>
                  </motion.li>
                ))}
              </ul>
            </motion.div>

            {/* Programme */}
            <motion.div 
              variants={containerVariants}
              initial="hidden"
              animate="visible"
              className="bg-white rounded-2xl shadow-lg p-8"
            >
              <h2 className="text-2xl font-bold text-gray-900 mb-6">Programme</h2>
              <div className="space-y-6">
                {course.program.map((week, index) => (
                  <motion.div
                    key={index}
                    variants={itemVariants}
                    className="flex space-x-4"
                  >
                    <div className="flex-shrink-0 w-12 h-12 bg-blue-100 rounded-full flex items-center justify-center">
                      <span className="text-blue-600 font-bold">S{week.week}</span>
                    </div>
                    <div>
                      <h3 className="text-lg font-semibold text-gray-900">{week.title}</h3>
                      <p className="text-gray-600">{week.content}</p>
                    </div>
                  </motion.div>
                ))}
              </div>
            </motion.div>
          </motion.div>

          {/* Sidebar avec animation */}
          <motion.div 
            initial={{ opacity: 0, x: 50 }}
            animate={{ opacity: 1, x: 0 }}
            transition={{ duration: 0.6, delay: 0.4 }}
            className="space-y-8"
          >
            {/* Informations pratiques */}
            <motion.div 
              whileHover={{ scale: 1.02 }}
              className="bg-white rounded-2xl shadow-lg p-8"
            >
              <h2 className="text-2xl font-bold text-gray-900 mb-6">Informations pratiques</h2>
              <div className="space-y-4">
                <div className="flex justify-between">
                  <span className="text-gray-600">Prix</span>
                  <span className="font-semibold text-blue-600">{course.price}</span>
                </div>
                <div className="flex justify-between">
                  <span className="text-gray-600">Durée</span>
                  <span className="font-semibold text-gray-900">{course.duration}</span>
                </div>
                <div className="flex justify-between">
                  <span className="text-gray-600">Niveau</span>
                  <span className="font-semibold text-gray-900">{course.level}</span>
                </div>
              </div>
              <motion.button
                whileHover={{ scale: 1.05 }}
                whileTap={{ scale: 0.95 }}
                className="w-full mt-6 bg-gradient-to-r from-blue-600 to-purple-600 text-white px-6 py-3 rounded-xl font-semibold hover:shadow-lg transition-all duration-300"
              >
                S'inscrire au cours
              </motion.button>
            </motion.div>

            {/* Professeur */}
            <motion.div 
              whileHover={{ scale: 1.02 }}
              className="bg-white rounded-2xl shadow-lg p-8"
            >
              <h2 className="text-2xl font-bold text-gray-900 mb-6">Votre professeur</h2>
              <div className="flex items-center space-x-4">
                <img
                  src={course.teacher.image}
                  alt={course.teacher.name}
                  className="w-16 h-16 rounded-full"
                />
                <div>
                  <h3 className="text-lg font-semibold text-gray-900">{course.teacher.name}</h3>
                  <p className="text-gray-600">{course.teacher.role}</p>
                  <p className="text-sm text-gray-500">{course.teacher.experience}</p>
                </div>
              </div>
            </motion.div>
          </motion.div>
        </div>
      </div>
    </div>
  );
};

export default CourseDetail;
