import React from 'react';
import CourseCard from '../components/CourseCard';
import './Courses.css';

const Courses = () => {
  // Données temporaires en attendant le backend
  const courses = [
    {
      id: 1,
      title: 'Guitare pour débutants',
      description: 'Apprenez les bases de la guitare en 10 leçons',
      level: 'Débutant',
      imageUrl: 'https://images.unsplash.com/photo-1510915361894-db8b60106cb1?ixlib=rb-1.2.1&auto=format&fit=crop&w=1350&q=80'
    },
    {
      id: 2,
      title: 'Piano intermédiaire',
      description: 'Perfectionnez votre technique au piano',
      level: 'Intermédiaire',
      imageUrl: 'https://images.unsplash.com/photo-1520523839897-bd0b52f945a0?ixlib=rb-1.2.1&auto=format&fit=crop&w=1350&q=80'
    },
    {
      id: 3,
      title: 'Chant avancé',
      description: 'Techniques avancées de chant et performance',
      level: 'Avancé',
      imageUrl: 'https://images.unsplash.com/photo-1511671782779-c97d3d27a1d4?ixlib=rb-1.2.1&auto=format&fit=crop&w=1350&q=80'
    }
  ];

  return (
    <div className="courses-page">
      <h1>Nos Cours de Musique</h1>
      <div className="courses-grid">
        {courses.map(course => (
          <CourseCard key={course.id} course={course} />
        ))}
      </div>
    </div>
  );
};

export default Courses;
