import React from 'react';
import { useParams } from 'react-router-dom';
import './CourseDetail.css';

const CourseDetail = () => {
  const { id } = useParams();

  // Données temporaires (à remplacer par l'API plus tard)
  const courseDetails = {
    1: {
      title: 'Guitare - Niveau Débutant',
      description: 'Découvrez les joies de la guitare avec notre cours complet pour débutants. Apprenez les bases essentielles, les accords fondamentaux et développez votre sens du rythme.',
      level: 'Débutant',
      imageUrl: 'https://images.unsplash.com/photo-1510915361894-db8b60106cb1?ixlib=rb-1.2.1&auto=format&fit=crop&w=1350&q=80',
      duration: '3 mois',
      price: '299€',
      objectives: [
        'Maîtriser les accords de base (majeur, mineur, 7ème)',
        'Apprendre les techniques de base (picking, strumming)',
        'Développer votre sens du rythme',
        'Jouer vos premiers morceaux complets',
        'Comprendre la théorie musicale de base'
      ],
      program: [
        {
          week: 1,
          title: 'Introduction à la guitare',
          content: 'Découverte de l\'instrument, accordage, position de base, premiers exercices'
        },
        {
          week: 2,
          title: 'Les accords de base',
          content: 'Apprentissage des accords majeurs et mineurs, exercices de transition'
        },
        {
          week: 3,
          title: 'Le rythme',
          content: 'Exercices rythmiques, premiers arpèges, techniques de main droite'
        },
        {
          week: 4,
          title: 'Premiers morceaux',
          content: 'Application des connaissances sur des morceaux simples, travail sur l\'interprétation'
        }
      ],
      requirements: [
        'Avoir une guitare (classique ou folk)',
        'Être motivé et assidu',
        'Avoir 30 minutes par jour pour pratiquer',
        'Aucune connaissance musicale préalable requise'
      ],
      teacher: {
        name: 'Jean Dupont',
        bio: 'Guitariste professionnel avec 15 ans d\'expérience. Spécialisé dans l\'enseignement aux débutants, Jean a formé plus de 500 élèves et se passionne pour la transmission de sa passion.',
        imageUrl: 'https://images.unsplash.com/photo-1507003211169-0a1dd7228f2d?ixlib=rb-1.2.1&auto=format&fit=crop&w=1350&q=80'
      }
    },
    2: {
      title: 'Piano - Niveau Débutant',
      description: 'Initiez-vous au piano avec notre cours complet pour débutants. Découvrez les bases du solfège, la position des mains et développez votre technique pianistique.',
      level: 'Débutant',
      imageUrl: 'https://images.unsplash.com/photo-1520523839897-bd0b52f945a0?ixlib=rb-1.2.1&auto=format&fit=crop&w=1350&q=80',
      duration: '3 mois',
      price: '299€',
      objectives: [
        'Maîtriser la position des mains',
        'Apprendre à lire les notes',
        'Développer l\'indépendance des mains',
        'Jouer des morceaux simples',
        'Comprendre les bases du solfège'
      ],
      program: [
        {
          week: 1,
          title: 'Découverte du piano',
          content: 'Position des mains, premiers exercices, découverte du clavier'
        },
        {
          week: 2,
          title: 'Lecture des notes',
          content: 'Apprentissage de la lecture des notes, exercices de lecture'
        },
        {
          week: 3,
          title: 'Indépendance des mains',
          content: 'Exercices pour développer l\'indépendance des mains'
        },
        {
          week: 4,
          title: 'Premiers morceaux',
          content: 'Application des connaissances sur des morceaux simples'
        }
      ],
      requirements: [
        'Avoir accès à un piano ou un clavier',
        'Être motivé et assidu',
        'Avoir 30 minutes par jour pour pratiquer',
        'Aucune connaissance musicale préalable requise'
      ],
      teacher: {
        name: 'Marie Laurent',
        bio: 'Pianiste classique avec 20 ans d\'expérience. Spécialisée dans l\'enseignement aux débutants, Marie a développé une méthode unique pour rendre l\'apprentissage du piano accessible à tous.',
        imageUrl: 'https://images.unsplash.com/photo-1494790108377-be9c29b29330?ixlib=rb-1.2.1&auto=format&fit=crop&w=1350&q=80'
      }
    },
    3: {
      title: 'Batterie - Niveau Débutant',
      description: 'Découvrez la batterie avec notre cours complet pour débutants. Apprenez les rudiments, développez votre coordination et votre sens du rythme.',
      level: 'Débutant',
      imageUrl: 'https://images.unsplash.com/photo-1519892300165-cb5542fb47c7?q=80&w=2070&auto=format&fit=crop&ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D',
      duration: '3 mois',
      price: '299€',
      objectives: [
        'Maîtriser les rudiments de base',
        'Développer la coordination des membres',
        'Apprendre les rythmes fondamentaux',
