  return (
    <div className="text-center">
      <h1 className="text-4xl font-bold mb-8">Bienvenue sur Music Learning App</h1>
      <p className="text-xl mb-8">Apprenez la musique Ã  votre rythme avec nos cours en ligne</p>
      <div className="space-x-4">
        <Link to="/register" className="bg-blue-600 text-white px-6 py-3 rounded-lg hover:bg-blue-700">
          Commencer maintenant
        </Link>
        <Link to="/courses" className="bg-gray-600 text-white px-6 py-3 rounded-lg hover:bg-gray-700">
          Voir les cours
        </Link>
      </div>
    </div>
  );
}

export default Home;
