import React, { useState, useEffect } from 'react';
import { useAuth } from '../contexts/AuthContext';

function AdminCourses() {
  const { user } = useAuth();
  const [courses, setCourses] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchCourses = async () => {
      try {
        // Simuler le chargement des données
        setCourses([
          {
            id: 1,
            title: 'Guitare Débutant',
            description: 'Apprenez les bases de la guitare',
            price: 49.99,
            duration: '10 heures',
            level: 'Débutant',
            instructor: 'Jean Dupont',
            students: 120
          },
          {
            id: 2,
            title: 'Piano Avancé',
            description: 'Perfectionnez votre technique au piano',
            price: 79.99,
            duration: '15 heures',
            level: 'Avancé',
            instructor: 'Marie Martin',
            students: 85
          },
          {
            id: 3,
            title: 'Batterie Intermédiaire',
            description: 'Maîtrisez les rythmes complexes',
            price: 59.99,
            duration: '12 heures',
            level: 'Intermédiaire',
            instructor: 'Pierre Durand',
            students: 65
          }
        ]);
        setIsLoading(false);
      } catch (err) {
        setError('Erreur lors du chargement des cours');
        setIsLoading(false);
      }
    };

    fetchCourses();
  }, []);

  if (!user?.isAdmin) {
    return (
      <div className="text-center py-12">
        <h2 className="text-2xl font-bold text-gray-900">Accès non autorisé</h2>
        <p className="mt-2 text-gray-600">Vous devez être administrateur pour accéder à cette page.</p>
      </div>
    );
  }

  if (isLoading) {
    return (
      <div className="text-center py-12">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-indigo-600 mx-auto"></div>
        <p className="mt-4 text-gray-600">Chargement des cours...</p>
      </div>
    );
  }

  if (error) {
    return (
      <div className="text-center py-12">
        <h2 className="text-2xl font-bold text-red-600">Erreur</h2>
        <p className="mt-2 text-gray-600">{error}</p>
      </div>
    );
  }

  return (
    <div className="py-6">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="flex justify-between items-center">
          <h1 className="text-3xl font-bold text-gray-900">Gestion des cours</h1>
          <button className="bg-indigo-600 text-white px-4 py-2 rounded-md hover:bg-indigo-700 transition-colors">
            Ajouter un cours
          </button>
        </div>

        <div className="mt-8">
          <div className="bg-white shadow rounded-lg">
            <div className="px-4 py-5 sm:px-6">
              <h3 className="text-lg leading-6 font-medium text-gray-900">Liste des cours</h3>
            </div>
            <div className="border-t border-gray-200">
              <div className="bg-white overflow-hidden">
                <table className="min-w-full divide-y divide-gray-200">
                  <thead className="bg-gray-50">
                    <tr>
                      <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Titre</th>
                      <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Description</th>
                      <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Prix</th>
                      <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Durée</th>
                      <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Niveau</th>
                      <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Instructeur</th>
                      <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Étudiants</th>
                      <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Actions</th>
                    </tr>
                  </thead>
                  <tbody className="bg-white divide-y divide-gray-200">
                    {courses.map((course) => (
                      <tr key={course.id}>
                        <td className="px-6 py-4 whitespace-nowrap text-sm font-medium text-gray-900">{course.title}</td>
                        <td className="px-6 py-4 text-sm text-gray-500">{course.description}</td>
                        <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">{course.price} €</td>
                        <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">{course.duration}</td>
                        <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">{course.level}</td>
                        <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">{course.instructor}</td>
                        <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">{course.students}</td>
                        <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                          <button className="text-indigo-600 hover:text-indigo-900 mr-3">Modifier</button>
                          <button className="text-red-600 hover:text-red-900">Supprimer</button>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default AdminCourses;
