import React, { useState } from 'react';
import CourseCard from '../components/CourseCard';
import './Courses.css';

const Courses = () => {
  const [selectedLevel, setSelectedLevel] = useState('all');

  const courses = [
    {
      id: 1,
      title: 'Guitare',
      description: 'Apprenez la guitare à votre rythme',
      level: 'Débutant',
      imageUrl: 'https://images.unsplash.com/photo-1510915361894-db8b60106cb1?ixlib=rb-1.2.1&auto=format&fit=crop&w=1350&q=80'
    },
    {
      id: 2,
      title: 'Guitare',
      description: 'Perfectionnez votre technique à la guitare',
      level: 'Intermédiaire',
      imageUrl: 'https://images.unsplash.com/photo-1510915361894-db8b60106cb1?ixlib=rb-1.2.1&auto=format&fit=crop&w=1350&q=80'
    },
    {
      id: 3,
      title: 'Guitare',
      description: 'Maîtrisez les techniques avancées de la guitare',
      level: 'Avancé',
      imageUrl: 'https://images.unsplash.com/photo-1510915361894-db8b60106cb1?ixlib=rb-1.2.1&auto=format&fit=crop&w=1350&q=80'
    },
    {
      id: 4,
      title: 'Piano',
      description: 'Découvrez les bases du piano',
      level: 'Débutant',
      imageUrl: 'https://images.unsplash.com/photo-1520523839897-bd0b52f945a0?ixlib=rb-1.2.1&auto=format&fit=crop&w=1350&q=80'
    },
    {
      id: 5,
      title: 'Piano',
      description: 'Développez votre technique pianistique',
      level: 'Intermédiaire',
      imageUrl: 'https://images.unsplash.com/photo-1520523839897-bd0b52f945a0?ixlib=rb-1.2.1&auto=format&fit=crop&w=1350&q=80'
    },
    {
      id: 6,
      title: 'Piano',
      description: 'Excellence pianistique et interprétation',
      level: 'Avancé',
      imageUrl: 'https://images.unsplash.com/photo-1520523839897-bd0b52f945a0?ixlib=rb-1.2.1&auto=format&fit=crop&w=1350&q=80'
    },
    {
      id: 7,
      title: 'Solfège',
      description: 'Apprenez les bases de la théorie musicale',
      level: 'Débutant',
      imageUrl: 'https://images.unsplash.com/photo-1511379938547-c1f69419868d?ixlib=rb-1.2.1&auto=format&fit=crop&w=1350&q=80'
    },
    {
      id: 8,
      title: 'Solfège',
      description: 'Approfondissez votre connaissance musicale',
      level: 'Intermédiaire',
      imageUrl: 'https://images.unsplash.com/photo-1511379938547-c1f69419868d?ixlib=rb-1.2.1&auto=format&fit=crop&w=1350&q=80'
    },
    {
      id: 9,
      title: 'Solfège',
      description: 'Maîtrisez l\'analyse musicale avancée',
      level: 'Avancé',
      imageUrl: 'https://images.unsplash.com/photo-1511379938547-c1f69419868d?ixlib=rb-1.2.1&auto=format&fit=crop&w=1350&q=80'
    },
    {
      id: 10,
      title: 'Trompette',
      description: 'Découvrez les bases de la trompette',
      level: 'Débutant',
      imageUrl: 'https://images.unsplash.com/photo-1511379938547-c1f69419868d?ixlib=rb-1.2.1&auto=format&fit=crop&w=1350&q=80'
    },
    {
      id: 11,
      title: 'Trompette',
      description: 'Perfectionnez votre technique à la trompette',
      level: 'Intermédiaire',
      imageUrl: 'https://images.unsplash.com/photo-1511379938547-c1f69419868d?ixlib=rb-1.2.1&auto=format&fit=crop&w=1350&q=80'
    },
    {
      id: 12,
      title: 'Trompette',
      description: 'Maîtrisez les techniques avancées de la trompette',
      level: 'Avancé',
      imageUrl: 'https://images.unsplash.com/photo-1511379938547-c1f69419868d?ixlib=rb-1.2.1&auto=format&fit=crop&w=1350&q=80'
    },
    {
      id: 13,
      title: 'Flûte',
      description: 'Apprenez les bases de la flûte',
      level: 'Débutant',
      imageUrl: 'https://images.unsplash.com/photo-1511379938547-c1f69419868d?ixlib=rb-1.2.1&auto=format&fit=crop&w=1350&q=80'
    },
    {
      id: 14,
      title: 'Flûte',
      description: 'Développez votre technique à la flûte',
      level: 'Intermédiaire',
      imageUrl: 'https://images.unsplash.com/photo-1511379938547-c1f69419868d?ixlib=rb-1.2.1&auto=format&fit=crop&w=1350&q=80'
    },
    {
      id: 15,
      title: 'Flûte',
      description: 'Excellence flûtistique et interprétation',
      level: 'Avancé',
      imageUrl: 'https://images.unsplash.com/photo-1511379938547-c1f69419868d?ixlib=rb-1.2.1&auto=format&fit=crop&w=1350&q=80'
    },
    {
      id: 16,
      title: 'Batterie',
      description: 'Découvrez les bases de la batterie',
      level: 'Débutant',
      imageUrl: 'https://images.unsplash.com/photo-1511379938547-c1f69419868d?ixlib=rb-1.2.1&auto=format&fit=crop&w=1350&q=80'
    },
    {
      id: 17,
      title: 'Batterie',
      description: 'Perfectionnez votre technique à la batterie',
      level: 'Intermédiaire',
      imageUrl: 'https://images.unsplash.com/photo-1511379938547-c1f69419868d?ixlib=rb-1.2.1&auto=format&fit=crop&w=1350&q=80'
    },
    {
      id: 18,
      title: 'Batterie',
      description: 'Maîtrisez les techniques avancées de la batterie',
      level: 'Avancé',
      imageUrl: 'https://images.unsplash.com/photo-1511379938547-c1f69419868d?ixlib=rb-1.2.1&auto=format&fit=crop&w=1350&q=80'
    },
    {
      id: 19,
      title: 'Guitare Basse',
      description: 'Apprenez les bases de la guitare basse',
      level: 'Débutant',
      imageUrl: 'https://images.unsplash.com/photo-1511379938547-c1f69419868d?ixlib=rb-1.2.1&auto=format&fit=crop&w=1350&q=80'
    },
    {
      id: 20,
      title: 'Guitare Basse',
      description: 'Développez votre technique à la guitare basse',
      level: 'Intermédiaire',
      imageUrl: 'https://images.unsplash.com/photo-1511379938547-c1f69419868d?ixlib=rb-1.2.1&auto=format&fit=crop&w=1350&q=80'
    },
    {
      id: 21,
      title: 'Guitare Basse',
      description: 'Excellence basse et interprétation',
      level: 'Avancé',
      imageUrl: 'https://images.unsplash.com/photo-1511379938547-c1f69419868d?ixlib=rb-1.2.1&auto=format&fit=crop&w=1350&q=80'
    }
  ];

  const filteredCourses = selectedLevel === 'all' 
    ? courses 
    : courses.filter(course => course.level === selectedLevel);

  return (
    <div className="courses-page">
      <h1>Nos Cours de Musique</h1>
      <div className="level-filter">
        <button 
          className={selectedLevel === 'all' ? 'active' : ''} 
          onClick={() => setSelectedLevel('all')}
        >
          Tous les niveaux
        </button>
        <button 
          className={selectedLevel === 'Débutant' ? 'active' : ''} 
          onClick={() => setSelectedLevel('Débutant')}
        >
          Débutant
        </button>
        <button 
          className={selectedLevel === 'Intermédiaire' ? 'active' : ''} 
          onClick={() => setSelectedLevel('Intermédiaire')}
        >
          Intermédiaire
        </button>
        <button 
          className={selectedLevel === 'Avancé' ? 'active' : ''} 
          onClick={() => setSelectedLevel('Avancé')}
        >
          Avancé
        </button>
      </div>
      <div className="courses-grid">
        {filteredCourses.map(course => (
          <CourseCard key={course.id} course={course} />
        ))}
      </div>
    </div>
  );
};

export default Courses;
