import React from 'react';

function Courses() {
  // Exemple de données de cours
  const courses = [
    {
      id: 1,
      title: 'Guitare pour débutants',
      description: 'Apprenez les bases de la guitare en 10 leçons',
      level: 'Débutant',
      price: 49.99
    },
    {
      id: 2,
      title: 'Piano intermédiaire',
      description: 'Perfectionnez votre technique au piano',
      level: 'Intermédiaire',
      price: 69.99
    },
    {
      id: 3,
      title: 'Chant avancé',
      description: 'Techniques avancées de chant et performance',
      level: 'Avancé',
      price: 89.99
    }
  ];

  return (
    <div className="container mx-auto px-4 py-8">
      <h1 className="text-3xl font-bold mb-8">Nos Cours</h1>
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
        {courses.map(course => (
          <div key={course.id} className="bg-white rounded-lg shadow-lg overflow-hidden">
            <div className="p-6">
              <h2 className="text-xl font-bold mb-2">{course.title}</h2>
              <p className="text-gray-600 mb-4">{course.description}</p>
              <div className="flex justify-between items-center">
                <span className="text-sm text-gray-500">Niveau: {course.level}</span>
                <span className="text-lg font-bold">{course.price}€</span>
              </div>
              <button className="mt-4 w-full bg-blue-600 text-white py-2 px-4 rounded-md hover:bg-blue-700">
                S'inscrire
              </button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Courses;
