import React, { useState, useEffect } from 'react';
import { useAuth } from '../contexts/AuthContext';

function AdminPayments() {
  const { user } = useAuth();
  const [payments, setPayments] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);
  const [selectedPayment, setSelectedPayment] = useState(null);
  const [showModal, setShowModal] = useState(false);

  useEffect(() => {
    const fetchPayments = async () => {
      try {
        // Simuler le chargement des données
        setPayments([
          {
            id: 1,
            user: 'Jean Dupont',
            userId: 1,
            course: 'Guitare Débutant',
            courseId: 1,
            amount: 49.99,
            date: '2024-03-15',
            status: 'Complété',
            paymentMethod: 'Orange Money',
            isUnlocked: false
          },
          {
            id: 2,
            user: 'Marie Martin',
            userId: 2,
            course: 'Piano Avancé',
            courseId: 2,
            amount: 79.99,
            date: '2024-03-14',
            status: 'Complété',
            paymentMethod: 'Orange Money',
            isUnlocked: true
          },
          {
            id: 3,
            user: 'Pierre Durand',
            userId: 3,
            course: 'Batterie Intermédiaire',
            courseId: 3,
            amount: 59.99,
            date: '2024-03-13',
            status: 'En attente',
            paymentMethod: 'Orange Money',
            isUnlocked: false
          }
        ]);
        setIsLoading(false);
      } catch (err) {
        setError('Erreur lors du chargement des paiements');
        setIsLoading(false);
      }
    };

    fetchPayments();
  }, []);

  const handleUnlockCourse = async (payment) => {
    setSelectedPayment(payment);
    setShowModal(true);
  };

  const confirmUnlock = async () => {
    try {
      // Ici, vous ajouterez l'appel API pour débloquer le cours
      const updatedPayments = payments.map(p => 
        p.id === selectedPayment.id ? { ...p, isUnlocked: true } : p
      );
      setPayments(updatedPayments);
      setShowModal(false);
      // Ajoutez ici l'appel API pour mettre à jour le statut dans la base de données
    } catch (err) {
      setError('Erreur lors du déblocage du cours');
    }
  };

  if (!user?.isAdmin) {
    return (
      <div className="text-center py-12">
        <h2 className="text-2xl font-bold text-gray-900">Accès non autorisé</h2>
        <p className="mt-2 text-gray-600">Vous devez être administrateur pour accéder à cette page.</p>
      </div>
    );
  }

  if (isLoading) {
    return (
      <div className="text-center py-12">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-indigo-600 mx-auto"></div>
        <p className="mt-4 text-gray-600">Chargement des paiements...</p>
      </div>
    );
  }

  if (error) {
    return (
      <div className="text-center py-12">
        <h2 className="text-2xl font-bold text-red-600">Erreur</h2>
        <p className="mt-2 text-gray-600">{error}</p>
      </div>
    );
  }

  return (
    <div className="py-6">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="flex justify-between items-center">
          <h1 className="text-3xl font-bold text-gray-900">Gestion des paiements</h1>
          <div className="flex space-x-4">
            <button className="bg-indigo-600 text-white px-4 py-2 rounded-md hover:bg-indigo-700 transition-colors">
              Exporter
            </button>
            <button className="bg-green-600 text-white px-4 py-2 rounded-md hover:bg-green-700 transition-colors">
              Générer rapport
            </button>
          </div>
        </div>

        <div className="mt-8">
          <div className="bg-white shadow rounded-lg">
            <div className="px-4 py-5 sm:px-6">
              <h3 className="text-lg leading-6 font-medium text-gray-900">Liste des paiements</h3>
            </div>
            <div className="border-t border-gray-200">
              <div className="bg-white overflow-hidden">
                <table className="min-w-full divide-y divide-gray-200">
                  <thead className="bg-gray-50">
                    <tr>
                      <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Utilisateur</th>
                      <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Cours</th>
                      <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Montant</th>
                      <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Date</th>
                      <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Statut</th>
                      <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Méthode de paiement</th>
                      <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Accès</th>
                      <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Actions</th>
                    </tr>
                  </thead>
                  <tbody className="bg-white divide-y divide-gray-200">
                    {payments.map((payment) => (
                      <tr key={payment.id}>
                        <td className="px-6 py-4 whitespace-nowrap text-sm font-medium text-gray-900">{payment.user}</td>
                        <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">{payment.course}</td>
                        <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">{payment.amount} €</td>
                        <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">{payment.date}</td>
                        <td className="px-6 py-4 whitespace-nowrap">
                          <span className={`px-2 inline-flex text-xs leading-5 font-semibold rounded-full ${
                            payment.status === 'Complété' ? 'bg-green-100 text-green-800' : 'bg-yellow-100 text-yellow-800'
                          }`}>
                            {payment.status}
                          </span>
                        </td>
                        <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">{payment.paymentMethod}</td>
                        <td className="px-6 py-4 whitespace-nowrap">
                          <span className={`px-2 inline-flex text-xs leading-5 font-semibold rounded-full ${
                            payment.isUnlocked ? 'bg-green-100 text-green-800' : 'bg-red-100 text-red-800'
                          }`}>
                            {payment.isUnlocked ? 'Débloqué' : 'Bloqué'}
                          </span>
                        </td>
                        <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                          <button 
                            onClick={() => handleUnlockCourse(payment)}
                            disabled={payment.isUnlocked}
                            className={`mr-3 ${
                              payment.isUnlocked 
                                ? 'text-gray-400 cursor-not-allowed' 
                                : 'text-indigo-600 hover:text-indigo-900'
                            }`}
                          >
                            Débloquer
                          </button>
                          <button className="text-red-600 hover:text-red-900">Annuler</button>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* Modal de confirmation */}
      {showModal && (
        <div className="fixed inset-0 bg-gray-500 bg-opacity-75 flex items-center justify-center">
          <div className="bg-white rounded-lg p-6 max-w-md w-full">
            <h3 className="text-lg font-medium text-gray-900 mb-4">Confirmer le déblocage</h3>
            <p className="text-gray-600 mb-4">
              Êtes-vous sûr de vouloir débloquer le cours "{selectedPayment?.course}" pour {selectedPayment?.user} ?
            </p>
            <div className="flex justify-end space-x-4">
              <button
                onClick={() => setShowModal(false)}
                className="px-4 py-2 text-gray-700 bg-gray-100 rounded-md hover:bg-gray-200"
              >
                Annuler
              </button>
              <button
                onClick={confirmUnlock}
                className="px-4 py-2 text-white bg-green-600 rounded-md hover:bg-green-700"
              >
                Confirmer
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default AdminPayments;
