import React from 'react';
import { useParams } from 'react-router-dom';
import './CourseDetail.css';

const CourseDetail = () => {
  const { id } = useParams();

  // Données temporaires (à remplacer par l'API plus tard)
  const courseDetails = {
    // Guitare
    1: {
      title: 'Guitare - Niveau Débutant',
      description: 'Découvrez les joies de la guitare avec notre cours complet pour débutants. Apprenez les bases essentielles, les accords fondamentaux et développez votre sens du rythme.',
      level: 'Débutant',
      imageUrl: 'https://images.unsplash.com/photo-1510915361894-db8b60106cb1?ixlib=rb-1.2.1&auto=format&fit=crop&w=1350&q=80',
      duration: '3 mois',
      price: '299€',
      objectives: [
        'Maîtriser les accords de base (majeur, mineur, 7ème)',
        'Apprendre les techniques de base (picking, strumming)',
        'Développer votre sens du rythme',
        'Jouer vos premiers morceaux complets',
        'Comprendre la théorie musicale de base'
      ],
      program: [
        {
          week: 1,
          title: 'Introduction à la guitare',
          content: 'Découverte de l\'instrument, accordage, position de base, premiers exercices'
        },
        {
          week: 2,
          title: 'Les accords de base',
          content: 'Apprentissage des accords majeurs et mineurs, exercices de transition'
        },
        {
          week: 3,
          title: 'Le rythme',
          content: 'Exercices rythmiques, premiers arpèges, techniques de main droite'
        },
        {
          week: 4,
          title: 'Premiers morceaux',
          content: 'Application des connaissances sur des morceaux simples, travail sur l\'interprétation'
        }
      ],
      requirements: [
        'Avoir une guitare (classique ou folk)',
        'Être motivé et assidu',
        'Avoir 30 minutes par jour pour pratiquer',
        'Aucune connaissance musicale préalable requise'
      ],
      teacher: {
        name: 'Jean Dupont',
        bio: 'Guitariste professionnel avec 15 ans d\'expérience. Spécialisé dans l\'enseignement aux débutants, Jean a formé plus de 500 élèves et se passionne pour la transmission de sa passion.',
        imageUrl: 'https://images.unsplash.com/photo-1507003211169-0a1dd7228f2d?ixlib=rb-1.2.1&auto=format&fit=crop&w=1350&q=80'
      }
    },
    2: {
      title: 'Guitare - Niveau Intermédiaire',
      description: 'Perfectionnez votre technique à la guitare avec notre cours intermédiaire. Développez votre style, apprenez des techniques avancées et enrichissez votre répertoire.',
      level: 'Intermédiaire',
      imageUrl: 'https://images.unsplash.com/photo-1510915361894-db8b60106cb1?ixlib=rb-1.2.1&auto=format&fit=crop&w=1350&q=80',
      duration: '3 mois',
      price: '349€',
      objectives: [
        'Maîtriser les techniques avancées (barrés, tapping, harmoniques)',
        'Développer votre style personnel',
        'Apprendre l\'improvisation',
        'Enrichir votre répertoire',
        'Comprendre l\'harmonie avancée'
      ],
      program: [
        {
          week: 1,
          title: 'Techniques avancées',
          content: 'Perfectionnement des barrés, tapping, harmoniques'
        },
        {
          week: 2,
          title: 'Improvisation',
          content: 'Apprentissage des gammes, modes, et techniques d\'improvisation'
        },
        {
          week: 3,
          title: 'Style personnel',
          content: 'Développement de votre style, travail sur l\'expression'
        },
        {
          week: 4,
          title: 'Répertoire avancé',
          content: 'Étude de morceaux complexes, travail sur l\'interprétation'
        }
      ],
      requirements: [
        'Avoir suivi le cours débutant ou équivalent',
        'Maîtriser les accords de base',
        'Avoir une bonne technique de main droite',
        'Être à l\'aise avec le rythme'
      ],
      teacher: {
        name: 'Marie Laurent',
        bio: 'Guitariste professionnelle avec 20 ans d\'expérience. Spécialisée dans l\'enseignement intermédiaire, Marie a développé une méthode unique pour aider les guitaristes à progresser.',
        imageUrl: 'https://images.unsplash.com/photo-1494790108377-be9c29b29330?ixlib=rb-1.2.1&auto=format&fit=crop&w=1350&q=80'
      }
    },
    3: {
      title: 'Guitare - Niveau Avancé',
      description: 'Maîtrisez les techniques avancées de la guitare avec notre cours expert. Développez votre virtuosité, apprenez des techniques complexes et créez votre propre style.',
      level: 'Avancé',
      imageUrl: 'https://images.unsplash.com/photo-1510915361894-db8b60106cb1?ixlib=rb-1.2.1&auto=format&fit=crop&w=1350&q=80',
      duration: '3 mois',
      price: '399€',
      objectives: [
        'Maîtriser les techniques virtuoses',
        'Développer votre style unique',
        'Créer vos propres compositions',
        'Maîtriser l\'improvisation avancée',
        'Comprendre l\'harmonie complexe'
      ],
      program: [
        {
          week: 1,
          title: 'Techniques virtuoses',
          content: 'Perfectionnement des techniques avancées, vitesse, précision'
        },
        {
          week: 2,
          title: 'Composition',
          content: 'Apprentissage de la composition, arrangement, harmonie avancée'
        },
        {
          week: 3,
          title: 'Improvisation avancée',
          content: 'Techniques d\'improvisation complexes, modes, gammes exotiques'
        },
        {
          week: 4,
          title: 'Style personnel',
          content: 'Développement d\'un style unique, signature artistique'
        }
      ],
      requirements: [
        'Avoir suivi le cours intermédiaire ou équivalent',
        'Maîtriser les techniques avancées',
        'Avoir une bonne expérience de l\'improvisation',
        'Être à l\'aise avec la composition'
      ],
      teacher: {
        name: 'Thomas Martin',
        bio: 'Guitariste virtuose avec 25 ans d\'expérience. Spécialisé dans l\'enseignement avancé, Thomas a formé de nombreux guitaristes professionnels et se passionne pour la transmission de son savoir.',
        imageUrl: 'https://images.unsplash.com/photo-1506794778202-cad84cf45f1d?ixlib=rb-1.2.1&auto=format&fit=crop&w=1350&q=80'
      }
    },
    // Piano
    4: {
      title: 'Piano - Niveau Débutant',
      description: 'Initiez-vous au piano avec notre cours complet pour débutants. Découvrez les bases du solfège, la position des mains et développez votre technique pianistique.',
      level: 'Débutant',
      imageUrl: 'https://images.unsplash.com/photo-1520523839897-bd0b52f945a0?ixlib=rb-1.2.1&auto=format&fit=crop&w=1350&q=80',
      duration: '3 mois',
      price: '299€',
      objectives: [
        'Maîtriser la position des mains',
        'Apprendre à lire les notes',
        'Développer l\'indépendance des mains',
        'Jouer des morceaux simples',
        'Comprendre les bases du solfège'
      ],
      program: [
        {
          week: 1,
          title: 'Découverte du piano',
          content: 'Position des mains, premiers exercices, découverte du clavier'
        },
        {
          week: 2,
          title: 'Lecture des notes',
          content: 'Apprentissage de la lecture des notes, exercices de lecture'
        },
        {
          week: 3,
          title: 'Indépendance des mains',
          content: 'Exercices pour développer l\'indépendance des mains'
        },
        {
          week: 4,
          title: 'Premiers morceaux',
          content: 'Application des connaissances sur des morceaux simples'
        }
      ],
      requirements: [
        'Avoir accès à un piano ou un clavier',
        'Être motivé et assidu',
        'Avoir 30 minutes par jour pour pratiquer',
        'Aucune connaissance musicale préalable requise'
      ],
      teacher: {
        name: 'Sophie Dubois',
        bio: 'Pianiste classique avec 20 ans d\'expérience. Spécialisée dans l\'enseignement aux débutants, Sophie a développé une méthode unique pour rendre l\'apprentissage du piano accessible à tous.',
        imageUrl: 'https://images.unsplash.com/photo-1534528741775-53994a69daeb?ixlib=rb-1.2.1&auto=format&fit=crop&w=1350&q=80'
      }
    },
    5: {
      title: 'Piano - Niveau Intermédiaire',
      description: 'Développez votre technique pianistique avec notre cours intermédiaire. Perfectionnez votre jeu, apprenez des techniques avancées et enrichissez votre répertoire.',
      level: 'Intermédiaire',
      imageUrl: 'https://images.unsplash.com/photo-1520523839897-bd0b52f945a0?ixlib=rb-1.2.1&auto=format&fit=crop&w=1350&q=80',
      duration: '3 mois',
      price: '349€',
      objectives: [
        'Maîtriser les techniques avancées',
        'Développer l\'expression musicale',
        'Apprendre l\'improvisation',
        'Enrichir votre répertoire',
        'Comprendre l\'harmonie avancée'
      ],
      program: [
        {
          week: 1,
          title: 'Techniques avancées',
          content: 'Perfectionnement des techniques, vitesse, précision'
